package basic.dev;

public class MainApp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		int tong = 0;
		do {
			i++;
			tong += i;
		}while(i <= 100);
		
		System.out.println("tong = " + tong);
		System.out.println("i =	" + i);
		
		}

	}


